mapper.select:
SELECT c_num,c_name,c_div,c_benefit,gc_idx,gc_gidx,gc_check,gc_issue,gc_deadline
FROM coupon c,g_coupon gc 
WHERE c.c_num=gc.gc_num AND gc_gidx=#{g_idx}

mapper.update:
update guest 
set g_card = #{g_card}, g_date = #{g_date}, g_cvc=#{g_cvc}
where g_idx = #{g_idx}

update guest 
set g_card = null, g_date = null, g_cvc= null
where g_idx = #{g_idx}

update guest 
set g_point = #{rePoint}
where g_idx = #{idx}

mapper.select:
SELECT (TO_DAYS(now()) - TO_DAYS(g_join_date)) AS joindate
FROM guest
WHERE g_idx=#{gidx}
